import React, { useEffect } from 'react';
import { HashLink as Link } from 'react-router-hash-link';

const AddMovie = () => {

    useEffect(() => {

    })

    const handleSave = async() =>{
        async function fetchMovies() {
            const response = await fetch('http://localhost:4003/graphql', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                query: `
                  {
                    movies {
                      id
                      name
                      genero
                      year
                    }
                  }
                `,
              }),
            });

          if (!response.ok) {
              console.error('Error en la solicitud GraphQL:', response.statusText);
              return;
          }

            const data = await response.json();
            console.log(data);
            //const moviesList = document.getElementById('product-list');
            //productList.innerHTML = '';

            /*data.data.movies.forEach(movie => {
              const li = document.createElement('li');
              li.textContent = `ID: ${product.id}, Nombre: ${product.name}, Valor: $${product.value}`;
              productList.appendChild(li);
            });*/
          }

          async function addMovies() {
            const movieName = document.getElementById('idNombre').value;
            const movieGenero = parseFloat(document.getElementById('idGenero').value);
            const movieYear = parseFloat(document.getElementById('idYear').value);

            if (!movieName || !movieGenero || !movieYear) {
              alert('Ingresa un nombre y un valor válido.');
              return;
            }

            const response = await fetch('http://localhost:4003/graphql', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                query: `
                  mutation {
                    addMovies(name: "${movieName}", genero: ${movieGenero}, year: ${movieYear}) {
                      id
                      name
                      genero,
                      year
                    }
                  }
                `,
              }),
            });

            await fetchMovies();
            //document.getElementById('productName').value = '';
            //document.getElementById('productValue').value = '';
          }

    }


    return (
        // Se agrega id para poder se enlazado desde el boton de otro componente
        <div className='addMovie' id='addmovie'>
            <form>
                <input type='text' id='idNombre' placeholder='Movie name' required ></input>
                <input type='text' id='idGenero' placeholder='Movie Genero' required ></input>
                <input type='text' id placeholder='Movie Year' ></input>
                <button>Add Movie</button>
            </form>
            <div className='top'>
                <Link smooth to='#header' className='top-button' onClick={handleSave}>Back to top</Link>
            </div>
        </div>
    )
}
export default AddMovie
