import React, { useEffect } from 'react';
import SingleMovie from '../components/SingleMovie';
const movies = [
    {name:'Rambo', genero: 'Action',year:2019},
    {name:'Rambo1', genero: 'Action',year:2020},
    {name:'Rambo2', genero: 'Action',year:2021},
    {name:'Rambo3', genero: 'Action',year:2022}
]



const Movies = () =>{
    // Inicializa los automaticos
useEffect(() => {

})

const handleSave = async() =>{
    async function fetchProducts() {
        const response = await fetch('http://localhost:4000/graphql', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            query: `
              {
                products {
                  id
                  name
                  value
                }
              }
            `,
          }),
        });

      if (!response.ok) {
          console.error('Error en la solicitud GraphQL:', response.statusText);
          return;
      }

        const data = await response.json();
        console.log(data);
        const productList = document.getElementById('product-list');
        productList.innerHTML = '';

        data.data.products.forEach(product => {
          const li = document.createElement('li');
          li.textContent = `ID: ${product.id}, Nombre: ${product.name}, Valor: $${product.value}`;
          productList.appendChild(li);
        });
      }

      async function addProduct() {
        const productName = document.getElementById('productName').value;
        const productValue = parseFloat(document.getElementById('productValue').value);

        if (!productName || isNaN(productValue)) {
          alert('Ingresa un nombre y un valor válido.');
          return;
        }

        const response = await fetch('http://localhost:4000/graphql', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            query: `
              mutation {
                addProduct(name: "${productName}", value: ${productValue}) {
                  id
                  name
                  value
                }
              }
            `,
          }),
        });

        await fetchProducts();
        document.getElementById('productName').value = '';
        document.getElementById('productValue').value = '';
      }

}

return(
    <div className='footer'>
    {
        movies.map((data) => {
            return <SingleMovie name={data.name} genero={data.genero + ' '+data.year}/>
        })
    }
    </div>
)
}
export default Movies
